import { createUploadthing, type FileRouter } from "uploadthing/next";
import { verifyJWT } from "@/lib/auth/session";
import { cookies } from "next/headers";

const f = createUploadthing();

// FileRouter for your app, can contain multiple FileRoutes
export const ourFileRouter = {
  // Define as many FileRoutes as you like, each with a unique routeSlug
  imageUploader: f({ image: { maxFileSize: "4MB", maxFileCount: 1 } })
    // Set permissions and file types for this FileRoute
    .middleware(async ({ req }) => {
      // This code runs on your server before upload
      const cookieStore = await cookies();
      const token = cookieStore.get("auth-token")?.value;

      if (!token) throw new Error("Unauthorized");

      try {
        const user = await verifyJWT(token);
        // Whatever is returned here is accessible in onUploadComplete as `metadata`
        return { userId: user.userId };
      } catch (e) {
        throw new Error("Unauthorized");
      }
    })
    .onUploadComplete(async ({ metadata, file }) => {
      // This code RUNS ON YOUR SERVER after upload
      console.log("Upload complete for userId:", metadata.userId);
      console.log("file url", file.url);

      // !!! Whatever is returned here is sent to the clientside onClientUploadComplete callback
      return { uploadedBy: metadata.userId };
    }),

  videoUploader: f({ video: { maxFileSize: "128MB", maxFileCount: 1 } })
    .middleware(async () => {
      const cookieStore = await cookies();
      const token = cookieStore.get("auth-token")?.value;
      if (!token) throw new Error("Unauthorized");

      const user = await verifyJWT(token);
      return { userId: user.userId };
    })
    .onUploadComplete(async ({ metadata, file }) => {
      console.log("Video upload complete:", file.url);
      return { fileUrl: file.url };
    }),
} satisfies FileRouter;

export type OurFileRouter = typeof ourFileRouter;

import { headers } from 'next/headers';
import { NextResponse } from 'next/server';
import { stripe } from '@/lib/stripe';
import Stripe from 'stripe';

const endpointSecret = process.env.STRIPE_WEBHOOK_SECRET;

export async function POST(req: Request) {
  const body = await req.text();
  const headersList = await headers();
  const sig = headersList.get('stripe-signature');

  let event: Stripe.Event;

  try {
    if (!sig || !endpointSecret) {
      // In development without a secret, we can skip signature verification
      // BUT for security, we should log it and proceed with caution
      console.warn('Webhook warning: Missing stripe-signature or STRIPE_WEBHOOK_SECRET');
      event = JSON.parse(body);
    } else {
      event = stripe.webhooks.constructEvent(body, sig, endpointSecret);
    }
  } catch (err: any) {
    console.error(`Webhook Error: ${err.message}`);
    return NextResponse.json({ error: `Webhook Error: ${err.message}` }, { status: 400 });
  }

  // Handle the event
  switch (event.type) {
    case 'checkout.session.completed':
      const session = event.data.object as Stripe.Checkout.Session;
      console.log(`≡ƒÆ░ Payment successful for session: ${session.id}`);
      // TODO: Update user subscription/points in database
      break;

    case 'invoice.payment_succeeded':
      const invoice = event.data.object as Stripe.Invoice;
      console.log(`Γ£à Invoice paid: ${invoice.id}`);
      // TODO: Handle recurring payment success
      break;

    case 'customer.subscription.deleted':
      const subscription = event.data.object as Stripe.Subscription;
      console.log(`Γ¥î Subscription cancelled: ${subscription.id}`);
      // TODO: Revoke access or downgrade user
      break;

    case 'charge.refunded':
      const charge = event.data.object as Stripe.Charge;
      console.log(`≡ƒöÖ Charge refunded: ${charge.id}`);
      // TODO: Handle refund logic
      break;

    default:
      console.log(`Unhandled event type ${event.type}`);
  }

  return NextResponse.json({ received: true });
}

