interface UploadZoneProps {
  onUploadComplete?: (url: string) => void
  maxSize?: number // in MB
  accept?: string
}

export function UploadZone({ onUploadComplete, maxSize = 10, accept = 'image/*' }: UploadZoneProps) {
  const [isDragging, setIsDragging] = useState(false)
  const [file, setFile] = useState<File | null>(null)
  const [progress, setProgress] = useState(0)
  const [status, setStatus] = useState<'idle' | 'uploading' | 'success' | 'error'>('idle')

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault()
    e.stopPropagation()
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setIsDragging(true)
    } else if (e.type === 'dragleave') {
      setIsDragging(false)
    }
  }

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault()
    e.stopPropagation()
    setIsDragging(false)

    const droppedFile = e.dataTransfer.files?.[0]
    if (droppedFile) handleFile(droppedFile)
  }

  const handleFile = (selectedFile: File) => {
    if (selectedFile.size > maxSize * 1024 * 1024) {
      alert(`File size exceeds ${maxSize}MB limit`)
      return
    }
    setFile(selectedFile)
    startUpload(selectedFile)
  }

  const startUpload = async (file: File) => {
    setStatus('uploading')
    setProgress(0)

    // Simulate upload
    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 95) {
          clearInterval(interval)
          return 95
        }
        return prev + 5
      })
    }, 100)

    try {
      // Here you would normally use UploadThing or your API
      setTimeout(() => {
        clearInterval(interval)
        setProgress(100)
        setStatus('success')
        onUploadComplete?.('https://cdn.heybirdy.com/sample-file.jpg')
      }, 2000)
    } catch (error) {
      setStatus('error')
    }
  }

  return (
    <div className="w-full">
      <div
        onDragEnter={handleDrag}
        onDragOver={handleDrag}
        onDragLeave={handleDrag}
        onDrop={handleDrop}
        className={`
          relative border-2 border-dashed rounded-lg p-12 transition-colors
          flex flex-col items-center justify-center text-center
          ${isDragging ? 'border-primary bg-primary/5' : 'border-muted-foreground/25'}
          ${status === 'success' ? 'border-green-500 bg-green-50' : ''}
        `}
      >
        {status === 'idle' && (
          <>
            <div className="p-4 bg-primary/10 rounded-full mb-4">
              <Upload className="w-8 h-8 text-primary" />
            </div>
            <p className="text-lg font-medium">Click or drag to upload</p>
            <p className="text-sm text-muted-foreground mt-1">
              {accept} up to {maxSize}MB
            </p>
            <input
              type="file"
              accept={accept}
              onChange={(e) => e.target.files?.[0] && handleFile(e.target.files[0])}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
            />
          </>
        )}

        {status === 'uploading' && (
          <div className="w-full max-w-xs">
            <File className="w-12 h-12 text-primary mx-auto mb-4 animate-pulse" />
            <p className="text-sm font-medium mb-2">Uploading {file?.name}...</p>
            <Progress value={progress} className="h-2" />
          </div>
        )}

        {status === 'success' && (
          <div className="flex flex-col items-center">
            <CheckCircle2 className="w-12 h-12 text-green-500 mb-4" />
            <p className="text-lg font-medium text-green-700">Upload Complete!</p>
            <p className="text-sm text-green-600 mb-4">{file?.name}</p>
            <Button variant="outline" size="sm" onClick={() => setStatus('idle')}>
              Upload another
            </Button>
          </div>
        )}
      </div>
    </div>
  )
}

export interface JWTPayload {
  userId: string;
  roles: string[];
  exp: number;
}

/**
 * Signs a JWT token with the provided payload.
 * In a real app, use 'jose' or 'jsonwebtoken' with a secret key.
 */
export async function signJWT(payload: Partial<JWTPayload>): Promise<string> {
  const header = { alg: 'HS256', typ: 'JWT' };
  const fullPayload = {
    ...payload,
    exp: Math.floor(Date.now() / 1000) + (60 * 60 * 24), // 24 hours
  };

  const encodedHeader = btoa(JSON.stringify(header));
  const encodedPayload = btoa(JSON.stringify(fullPayload));
  const signature = 'mock-signature'; // Placeholder signature

  return `${encodedHeader}.${encodedPayload}.${signature}`;
}

/**
 * Verifies a JWT token and returns the payload.
 * In a real app, use 'jose' or 'jsonwebtoken' with a secret key.
 */
export async function verifyJWT(token: string): Promise<JWTPayload> {
  // This is a placeholder. In production, verify the signature.
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      throw new Error('Invalid token format');
    }

    const payload = JSON.parse(atob(parts[1]));
    return payload as JWTPayload;
  } catch (error) {
    throw new Error('Invalid token');
  }
}

import { NextResponse } from 'next/server' 
import type { NextRequest } from 'next/server' 
import { verifyJWT } from './lib/auth/session' 

// Define protected routes and their required roles 
const protectedRoutes = { 
  '/creator': ['creator', 'admin'], 
  '/affiliate': ['affiliate', 'creator', 'admin'], 
  '/admin': ['admin'], 
} as const 

export async function proxy(request: NextRequest) { 
  const { pathname } = request.nextUrl 

  // Check if route is protected 
  const entry = Object.entries(protectedRoutes).find(([route]) => 
    pathname.startsWith(route) 
  )

  const requiredRoles = entry ? entry[1] : null;

  if (!requiredRoles) { 
    return NextResponse.next() 
  } 

  // Get token from cookies 
  const token = request.cookies.get('auth-token')?.value 

  if (!token) { 
    return redirectToLogin(request) 
  } 

  try { 
    const payload = await verifyJWT(token) 

    // Check if user has required role 
    const hasRole = requiredRoles.some(role => 
      payload.roles.includes(role) 
    ) 

    if (!hasRole) { 
      return redirectToUnauthorized(request) 
    } 

    // Add user info to request headers 
    const requestHeaders = new Headers(request.headers) 
    requestHeaders.set('x-user-id', payload.userId) 
    requestHeaders.set('x-user-roles', JSON.stringify(payload.roles)) 

    return NextResponse.next({ 
      request: { 
        headers: requestHeaders, 
      }, 
    }) 
  } catch (error) { 
    return redirectToLogin(request) 
  } 
} 

function redirectToLogin(request: NextRequest) { 
  const loginUrl = new URL('/login', request.url) 
  loginUrl.searchParams.set('from', request.nextUrl.pathname) 
  return NextResponse.redirect(loginUrl) 
} 

function redirectToUnauthorized(request: NextRequest) { 
  return NextResponse.redirect(new URL('/unauthorized', request.url)) 
} 

